#ifndef _SOCKPAIR_H_
#define _SOCKPAIR_H_

class SocketPair
{
public:
    SocketPair();
    ~SocketPair();

    int Init();
    int Close();
    int SetBlocking(BOOL);
    int Read(unsigned char*, int size);
    int Write(unsigned char*, int size);

    int GetReadFD();
    int GetWriteFD();

private:
    BOOL m_bBlocking;
#ifdef _WIN32
    SOCKET m_r;
    SOCKET m_w;
#else
    int m_r;
    int m_w;
#endif
};

#endif /* _SOCKPAIR_H_ */
